<?phprequire_once( dirname(dirname(dirname(dirname(dirname(dirname(__FILE__)))))).'/wp-load.php' );require_once( dirname(dirname(__FILE__)) . '/class/class-sb-helper.php');if (isset($_SERVER['REQUEST_METHOD']) && $_SERVER['REQUEST_METHOD'] !== 'POST') {    header('Content-Type: text/plain'); // merged from WP #9093    die('This file only accepts POST requests only.');}global $HTTP_RAW_POST_DATA;if (empty($HTTP_RAW_POST_DATA)) {    $data = file_get_contents('php://input');} else {    $data = & $HTTP_RAW_POST_DATA;}$sb_connector = new Smart_SB_Connector();$sb_connector->ProcessRequest($data);exit;?>